import React, { Component } from 'react'
import { Link } from 'react-router-dom'
import Menu from './Menu';
 
class Login extends Component {
  
    constructor(props) {
        super(props);
        this.state = {
         submitted: false, // Indique si on a validé le nom
         email:"",
         password:"",
         error:{}
        };
      }
     
      // Affiche un message d'erreur s'il n'y a pas de nom renseigné
  getWarning() {
    if (this.state.submitted && (this.state.email === '' || this.state.password === '' )) {
      return (
        <p> Vous devez introduir l'email' <b>et</b> le mot de passe !</p>
      )
    }
  }

      submitName(event) {
        this.props.submitForm(event)
        this.setState({submitted: true})
        
      }

      handleSubmit = async e => {
        e.preventDefault();
        const user={
          email:this.state.email,
          password:this.state.password
        }
        const response = await fetch('/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({user }),
        });
        const body = await response.text();
        
        this.setState({ submitted : true });
      };
      

      render() {
        return (
          <div id="container">
            <form  className="App-form" onSubmit={this.handleSubmit}>
                <div className="App-form-group">   
                     <h1>Connexion</h1>
                     <label><b>E-mail</b></label>
                     <input 
                      type="text" 
                      placeholder="Entrer e-mail" 
                      name="email"
                      onChange={(event) => this.props.handleChange(event)}
                     ></input>
                     <label><b>password</b></label>
                     <input 
                     type="password" 
                     placeholder="Entrer le mot de passe" 
                     name="password" 
                     onChange={(event) => this.props.handleChange(event)}
                     ></input>
                    <Link type="submit" className="App-btn"  ><b>Login</b></Link>
                 </div>   
           </form>
           <p>le nom : {this.props.username}
                 {this.getWarning()}</p>

          </div>
        );
      }

}

export default Login;
